'use strict';var cluster=require("cluster"),zmq=require("../"),port="tcp://127.0.0.1:12345";
if(cluster.isMaster){for(var i=0;2>i;i++)cluster.fork();cluster.on("death",function(a){console.log("worker "+a.pid+" died")});var socket=zmq.socket("push");socket.identity="upstream"+process.pid;socket.bind(port,function(a){if(a)throw a;console.log("bound!");setInterval(function(){var a=new Date;console.log(socket.identity+": sending data "+a.toString());socket.send(a.toString())},500)})}else socket=zmq.socket("pull"),socket.identity="downstream"+process.pid,socket.connect(port),console.log("connected!"),
socket.on("message",function(a){console.log(socket.identity+": received data "+a.toString())});