'use strict';var zmq=require(".."),should=require("should"),semver=require("semver");
describe("context",function(){it("should support setting max io threads",function(a){if(!semver.gte(zmq.version,"3.2.0"))return a(),console.warn("Test requires libzmq \x3e\x3d 3.2.0");zmq.Context.setMaxThreads(3);zmq.Context.getMaxThreads().should.equal(3);zmq.Context.setMaxThreads(1);a()});it("should support setting max number of sockets",function(a){if(!semver.gte(zmq.version,"3.2.0"))return a(),console.warn("Test requires libzmq \x3e\x3d 3.2.0");var b=zmq.Context.getMaxSockets();zmq.Context.setMaxSockets(256);
zmq.Context.getMaxSockets().should.equal(256);zmq.Context.setMaxSockets(b);a()})});