'use strict';var zmq=require(".."),should=require("should"),semver=require("semver"),version=semver.gte(zmq.version,"3.2.0");
describe("socket.error-callback",function(){var a;if(!version)return console.warn("ZMQ_ROUTER_MANDATORY requires libzmq 3.2.0, skipping test");it("should create a socket and set ZMQ_ROUTER_MANDATORY",function(){a=zmq.socket("router");a.setsockopt(zmq.ZMQ_ROUTER_MANDATORY,1)});it("should callback with error when not connected",function(b){a.send(["foo","bar"],null,function(a){a.should.be.an.instanceof(Error);b()})})});